    state = {
        posts: [],
    };

    componentDidMount() {
    firestore.collection('posts').get().then((snapshot) => {

        const posts = snapshot.docs.map(doc => {
            return {
                id: doc.id,
                ... doc.data(),
            }
        });
        console.log(snapshot);
        this.setState({
            posts,
        })
